mapper.select:
SELECT *
FROM TB_GBOOK G, TB_USER U
WHERE G.USER_NO = U.USER_NO AND PLACE_NO = #{place_no}

SELECT * FROM (SELECT ROWNUM AS RNUM, T1.* FROM (SELECT G.*, U.USER_NAME, P.PLACE_NAME, P.PLACE_IMG1 FROM TB_GBOOK G, TB_USER U , TB_PLACE P WHERE G.USER_NO = U.USER_NO AND G.PLACE_NO = P.PLACE_NO
AND (P.PLACE_NAME LIKE '%'||#{keyword}||'%' OR G.GBOOK_CONTENT LIKE '%'||#{keyword}||'%')
AND 1 = 1
ORDER BY GBOOK_DATE DESC ORDER BY GBOOK_DATE ASC
) T1 WHERE ROWNUM <= #{listSize}) WHERE RNUM > #{startList}

mapper.update:
UPDATE TB_GBOOK
SET
GBOOK_CONTENT = #{gbook_content}
WHERE GBOOK_NO = #{gbook_no}

mapper.select:

	UPDATE TB_PLACE P
	SET P.GBOOK_CNT = (SELECT COUNT(GBOOK_NO) FROM TB_GBOOK WHERE PLACE_NO = #{place_no})
	WHERE P.PLACE_NO = #{place_no}
